import axios from 'axios';
async function getInfo(){
    const res=await axios.get(`${process.env.VUE_APP_SERVER_URL}/readdept/`)
    console.log(res)
    return res.data
}
async function postInfo(val){
    var arr=[]
    await axios.post(`${process.env.VUE_APP_SERVER_URL}/createdept/`,val)
    .then((res) =>{
        this.arr = res.data
    })
    return arr
}
async function editInfo(val){
    var arr=[]
    await axios.put(`${process.env.VUE_APP_SERVER_URL}/updatedept/`,val)
    .then((res) =>{
        this.arr = res.data
    })
    return arr
}
async function deleteInfo(id){
    return await axios.delete(`${process.env.VUE_APP_SERVER_URL}/deletedept/${id}`)
}
export{getInfo,postInfo,editInfo,deleteInfo}
