import Vue from 'vue';
import axios from 'axios';
import VueAxios from 'vue-axios';
import searchGlobal from './searchGlobal.vue';
var test;
Vue.use(VueAxios, axios);
export default {
  name: 'api-integration',

  data() {
    return {
      empid: '',
      numberRules: [v => !!v || 'input is required', v => !isNaN(parseFloat(v)) && v >= 0 && v <= 99999 || 'input must be number && max 5 numbers'],
      name: '',
      nameRules: [v => !!v || 'input is required', v => v && v.length <= 20 || 'input must be less than 15 characters', v => /^[a-zA-z]+$/.test(v) || 'input is not valid'],
      dept: '',
      list: [],
      dialog: false,
      arr: [],
      flag: true,
      message: ''
    };
  },

  mounted() {
    Vue.axios.get('http://127.0.0.1:40193/readdept').then(resp => {
      this.list = resp.data;
    });
  },

  methods: {
    async sendData() {
      await Vue.axios.post(`http://127.0.0.1:40193/createdept`, {
        empid: this.empid,
        name: this.name,
        dept: this.dept
      });
      this.dialog = false;
      Vue.axios.get('http://127.0.0.1:40193/readdept').then(resp => {
        this.list = resp.data;
      });
      this.$refs.form.reset();
    },

    async deleting(id) {
      await Vue.axios.delete(`http://127.0.0.1:40193/deletedept/${id}`);
      Vue.axios.get('http://127.0.0.1:40193/readdept').then(resp => {
        this.list = resp.data;
      });
    },

    editItem(item) {
      this.flag = false;
      this.dialog = true;
      test = item;
      this.empid = item.empid;
      this.name = item.name;
      this.dept = item.dept;
    },

    async edit() {
      this.flag = true;
      this.dialog = false;
      test.empid = this.empid;
      test.name = this.name;
      test.dept = this.dept;
      this.$refs.form.reset();
      await axios.put(`http://127.0.0.1:40193/updatedept/${test.id}`, {
        empid: test.empid,
        name: test.name,
        dept: test.dept
      }).then(response => {
        console.log(response);
      });
      console.log(test.name);
      this.sendData();
    },

    change(message) {
      this.message = message;
    },

    funSearch(val) {
      this.list = val.data;
    }

  },
  components: {
    searchGlobal
  }
};
